mapper.update:
UPDATE t_equip_repair
SET
remark = #{equipRepair.remark}
WHERE id = #{equipRepair.id}

mapper.select:
SELECT t.id AS id, t.equip_info_id AS equipInfoId, t.type_id AS typeId, t.repair_point AS repairPoint, t.repairer AS repairer, t.repair_time AS repairTime, t.failure_cause AS failureCause, t.remark AS remark, t1.name AS equipName, t1.status AS status, t2.name AS typeName FROM t_equip_repair t, t_equip_info t1, t_type t2 WHERE t.equip_info_id = t1.id AND t.type_id = t2.id AND t1.status IN(1,2,3)
AND t1.name = #{equipName}
AND t2.name = #{typeName}
AND t.repair_time >= #{beginTime}
AND t.repair_time <= #{endTime}
